import React, { useState, useEffect } from 'react';
import { submitGrade, getAssessment } from '../services/api';

const GradeSubmission = ({ score, assessment, totalQuestions, correctAnswers }) => {
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);
  const [status, setStatus] = useState(assessment?.status || 'pending');

  useEffect(() => {
    // Check if assessment is already completed
    if (assessment?.status === 'success') {
      setSubmitted(true);
      setStatus('success');
      return;
    }

    // Polling to check the status of the grade submission
    const interval = setInterval(async () => {
      if (status === 'pending' && assessment?._id) {
        try {
          const updatedAssessment = await getAssessment(assessment._id);
          setStatus(updatedAssessment.status);
          
          if (updatedAssessment.status === 'success') {
            setSubmitted(true);
            clearInterval(interval);
          } else if (updatedAssessment.status === 'failed') {
            setError('Grade submission to Moodle failed');
            clearInterval(interval);
          }
        } catch (err) {
          console.error('Error fetching assessment status:', err);
        }
      }
    }, 3000); // Poll every 3 seconds

    return () => clearInterval(interval);
  }, [status, assessment?._id, assessment?.status]);

  const handleSubmitGrade = async () => {
    if (!assessment) {
      setError('No assessment data available');
      return;
    }

    setSubmitting(true);
    setError(null);

    try {
      const response = await submitGrade(assessment._id);
      
      // If the grade submission is successful, start polling for status updates
      if (response.success) {
        setStatus('pending');
        // The polling in useEffect will handle status updates
      }
    } catch (err) {
      setError(err.message || 'Failed to submit grade to LMS');
      setSubmitting(false);
    }
  };

  const getStatusMessage = () => {
    switch (status) {
      case 'success':
        return '✓ Grade successfully saved to Moodle gradebook!';
      case 'failed':
        return '✗ Failed to save grade to Moodle gradebook. Please try again.';
      case 'pending':
        return submitting ? 'Submitting grade to Moodle...' : 'Grade submission in progress...';
      default:
        return '';
    }
  };

  const getStatusClass = () => {
    switch (status) {
      case 'success':
        return 'success-message';
      case 'failed':
        return 'error-message';
      case 'pending':
        return 'pending-message';
      default:
        return '';
    }
  };

  return (
    <div className="grade-submission">
      <h2>Assessment Complete</h2>
      <div className="score-display">
        <div className="score-circle">
          <span className="score-value">{Math.round(score)}%</span>
        </div>
        <p>You got {correctAnswers} out of {totalQuestions} questions correct</p>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      {!submitted && status !== 'pending' ? (
        <button
          className="submit-grade-button"
          onClick={handleSubmitGrade}
          disabled={submitting}
        >
          {submitting ? 'Submitting...' : 'Save Grade to Moodle Gradebook'}
        </button>
      ) : (
        <div className={getStatusClass()}>
          <p>{getStatusMessage()}</p>
          {status === 'pending' && (
            <div className="loading-spinner">
              <div className="spinner"></div>
            </div>
          )}
        </div>
      )}

      <div className="lti-info">
        <h3>Assessment Information</h3>
        <p><strong>Assessment ID:</strong> {assessment?._id}</p>
        <p><strong>User ID:</strong> {assessment?.userId}</p>
        <p><strong>Score:</strong> {assessment?.scoreGiven} / {assessment?.scoreMaximum}</p>
        <p><strong>Status:</strong> <span className={`status-${status}`}>{status.toUpperCase()}</span></p>
        {assessment?.submittedAt && (
          <p><strong>Submitted At:</strong> {new Date(assessment.submittedAt).toLocaleString()}</p>
        )}
      </div>
    </div>
  );
};

export default GradeSubmission;
