// client/src/components/GradeSubmission.jsx
import React, { useState } from 'react';
import { submitGrade } from '../services/api';

const GradeSubmission = ({ score, assessment, totalQuestions, correctAnswers }) => {
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);
  const [currentAssessment, setCurrentAssessment] = useState(assessment);
  
  const handleSubmitGrade = async () => {
    if (!currentAssessment) {
      setError('No assessment data available');
      return;
    }
    
    setSubmitting(true);
    
    try {
      const result = await submitGrade(currentAssessment._id);
      // Update the assessment with the latest status
      setCurrentAssessment({
        ...currentAssessment,
        passbackStatus: result.assessment.passbackStatus || 'success',
        gradingProgress: 'FullyGraded'
      });
      setSubmitted(true);
    } catch (err) {
      setError(err.message || 'Failed to submit grade to LMS');
    } finally {
      setSubmitting(false);
    }
  };
  
  return (
    <div className="grade-submission">
      <h2>Assessment Complete</h2>
      
      <div className="score-display">
        <div className="score-circle">
          <span className="score-value">{Math.round(score)}%</span>
        </div>
        <p>You got {correctAnswers} out of {totalQuestions} questions correct</p>
      </div>
      
      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}
      
      {!submitted ? (
        <button 
          className="submit-grade-button"
          onClick={handleSubmitGrade}
          disabled={submitting}
        >
          {submitting ? 'Submitting...' : 'Send Grade to Gradebook'}
        </button>
      ) : (
        <div className="success-message">
          <p>âœ“ Grade successfully submitted to your LMS gradebook!</p>
        </div>
      )}
      
      <div className="lti-info">
        <h3>LTI Grade Passback Information</h3>
        <p>Assessment ID: {currentAssessment?._id}</p>
        <p>User ID: {currentAssessment?.userId}</p>
        <p>Score: {currentAssessment?.scoreGiven} / {currentAssessment?.scoreMaximum}</p>
        <p>Status: {currentAssessment?.gradingProgress || 'FullyGraded'}</p>
      </div>
    </div>
  );
};

export default GradeSubmission;
