// client/src/components/GradeSubmission.jsx

import React, { useState } from 'react';
import { submitGrade, getUserAssessments } from '../services/api';

const GradeSubmission = ({ score, assessment, totalQuestions, correctAnswers }) => {
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);
  const [updatedAssessment, setUpdatedAssessment] = useState(assessment);

  const handleSubmitGrade = async () => {
    if (!assessment) {
      setError('No assessment data available');
      return;
    }

    setSubmitting(true);

    try {
      const result = await submitGrade(assessment._id);
      setSubmitted(true);

      // Fetch the updated assessment data
      const updatedAssessmentData = await getUserAssessments(assessment.userId);
      const currentAssessment = updatedAssessmentData.assessments.find(a => a._id === assessment._id);
      setUpdatedAssessment(currentAssessment);

    } catch (err) {
      setError(err.message || 'Failed to submit grade to LMS');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="grade-submission">
      <h2>Assessment Complete</h2>

      <div className="score-display">
        <div className="score-circle">
          <span className="score-value">{Math.round(score)}%</span>
        </div>
        <p>You got {correctAnswers} out of {totalQuestions} questions correct</p>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      {!submitted ? (
        <button
          className="submit-grade-button"
          onClick={handleSubmitGrade}
          disabled={submitting}
        >
          {submitting ? 'Submitting...' : 'Send Grade to Gradebook'}
        </button>
      ) : (
        <div className="success-message">
          <p>âœ“ Grade successfully submitted to your LMS gradebook!</p>
        </div>
      )}

      <div className="lti-info">
        <h3>LTI Grade Passback Information</h3>
        <p>Assessment ID: {updatedAssessment?._id}</p>
        <p>User ID: {updatedAssessment?.userId}</p>
        <p>Score: {updatedAssessment?.scoreGiven} / {updatedAssessment?.scoreMaximum}</p>
        <p>Status: {updatedAssessment?.activityProgress || 'pending'}</p>
      </div>
    </div>
  );
};

export default GradeSubmission;
